The collections correspond to a given moment in the charts;
i.e. as you move over the charts, the corresponding collections
are highlighted/displayed in the table.

{
  $jsonSchema: {
    required: ['customer'], // the customer field is required
    properties: {
      purchaseMethod: {
        enum: ['In Store','Online'],
        description: "can only be either 'In Store' or 'Online'"
      }
    }
  }
}

{
   $jsonSchema: {
      bsonType: "object",
      required: [ "name", "year", "major", "gpa", "address.city", "address.street" ],
      properties: {
         name: {
            bsonType: "string",
            description: "must be a string"
         },
         year: {
            bsonType: "int",
            minimum: 2017,
            maximum: 3017,
            exclusiveMaximum: false,
            description: "must be an integer in [ 2017, 3017 ]"
         },
         major: {
            bsonType: "string",
            enum: [ "Math", "English", "Computer Science", "History", null ],
            description: "can only be one of the enum values"
         },
         gpa: {
            bsonType: [ "double" ],
            minimum: 0,
            description: "must be a double"
         }
      }
   }
}
